import React from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

const orders = [
  {
    id: 'ORD12345',
    date: 'March 10, 2025',
    total: 120.50,
    status: 'Delivered',
    paymentMethod: 'PayPal',
    items: [
      { name: 'Nike Shoes', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRflb85cfaM7ltPsVKm2uIAaKba1JAo_0eS3w&s' },
      { name: 'Adidas T-shirt', image: 'data:image/jpeg;base64,' },
    ],
  },
  {
    id: 'ORD67890',
    date: 'March 5, 2025',
    total: 75.99,
    status: 'Shipped',
    paymentMethod: 'Credit Card',
    items: [
      { name: 'Apple AirPods', image: 'data:image/png;base64,' },
    ],
  },
  {
    id: 'ORD11223',
    date: 'Feb 20, 2025',
    total: 99.99,
    status: 'Cancelled',
    paymentMethod: 'Debit Card',
    items: [
      { name: 'Samsung Watch', image: 'data:image/jpeg;base64,' },
    ],
  },
];

const OrderHistoryScreen = () => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'Delivered': return '#28a745'; // Green
      case 'Shipped': return '#007bff';  // Blue
      case 'Cancelled': return '#dc3545'; // Red
      default: return '#6c757d'; // Gray
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>ðŸ›’ Order History</Text>
      <FlatList
        data={orders}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={styles.orderCard}>
            <View style={styles.row}>
              <MaterialIcons name="receipt-long" size={22} color="#555" />
              <Text style={styles.orderId}> {item.id}</Text>
            </View>
            <Text style={styles.date}>ðŸ“… {item.date}</Text>
            <FlatList
              data={item.items}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({ item }) => (
                <View style={styles.itemRow}>
                  <Image source={{ uri: item.image }} style={styles.itemImage} />
                  <Text style={styles.itemName}>{item.name}</Text>
                </View>
              )}
            />
            <Text style={styles.total}>ðŸ’° Total: ${item.total.toFixed(2)}</Text>
            <Text style={[styles.status, { color: getStatusColor(item.status) }]}>
              âš¡ {item.status}
            </Text>
            <Text style={styles.payment}>ðŸ’³ {item.paymentMethod}</Text>
            <TouchableOpacity style={styles.viewButton}>
              <Text style={styles.viewButtonText}>View Details</Text>
            </TouchableOpacity>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: '#F5F9FF' },
  title: { fontSize: 26, fontWeight: 'bold', textAlign: 'center', marginBottom: 20 },
  orderCard: {
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 12,
    borderRadius: 12,
    elevation: 4,
    shadowColor: '#000',
    shadowOpacity: 0.1,
    shadowRadius: 5,
  },
  row: { flexDirection: 'row', alignItems: 'center', marginBottom: 5 },
  orderId: { fontSize: 16, fontWeight: 'bold', color: '#333' },
  date: { fontSize: 14, color: '#555', marginBottom: 3 },
  total: { fontSize: 16, fontWeight: 'bold', color: '#000' },
  status: { fontSize: 16, fontWeight: 'bold', marginTop: 5 },
  payment: { fontSize: 14, color: '#777', marginTop: 5 },
  itemRow: { flexDirection: 'row', alignItems: 'center', marginVertical: 5 },
  itemImage: { width: 50, height: 50, borderRadius: 5, marginRight: 10 },
  itemName: { fontSize: 14, color: '#333' },
  viewButton: {
    marginTop: 10,
    backgroundColor: '#007bff',
    padding: 12,
    borderRadius: 8,
    alignItems: 'center',
  },
  viewButtonText: { color: '#fff', fontWeight: 'bold', fontSize: 16 },
});

export default OrderHistoryScreen;
